import React, { useState } from "react";
import MaterialTable from "material-table";
import { Checkbox } from "semantic-ui-react";
import "./job.css";
import '../Pages.css';
import CreateIcon from '@mui/icons-material/Create';
import AddCircleRoundedIcon from '@mui/icons-material/AddCircleRounded';
import DeleteIcon from '@mui/icons-material/Delete';
import {Icon} from 'semantic-ui-react';
import {
  jobData_withoutcomplete,
  jobData_withcomplete,
} from "../../../Data/JobData";

export default function Job() {
  const [selected, toggleselected] = useState(false);
  
  const columns = [
    {
      title: "Status",
      field: "status",
     
     
      lookup: { '0': <div><Icon name='blue circle outline' size='large'/> Raised</div>,
      '1': <div><Icon name='blue circle' size='large'/> Raised and Assigned</div>,
      '2': <div><Icon name='orange circle' size='large'/> In Progress</div>,
      '3': <div><Icon name='red circle' size='large'/> On Hold</div>,
      '4': <div><Icon name='green circle' size='large'/> Completed</div>,
      } ,
      render: rowData => rowData==='0'?<Icon name='blue circle outline' size='large'/>:
      rowData==='1'?<Icon name='blue circle'size='large'/>:
      rowData==='2'?<Icon name='orange circle'size='large'/>:
      rowData==='3'?<Icon name='red circle'size='large'/>:
      <Icon name='green circle' size='large'/>


},
    { title: "Priority", field: "priority" ,
    lookup: { '0': 'Low', '1': 'Normal', '2': 'High', '3': 'Safety'},
  },
    { title: "Description", field: "description" },
    { title: "Category", field: "category" ,
    lookup: { '0': 'None', '1': 'Equipment', '2': 'Paddock', '3': 'Fuel Tank', '4': 'Silo', '5': 'Livestock'},},
    { title: "Asset", field: "asset" },
    { title: "Due Date", field: "duedate" },
    { title: "Last Update", field: "lastupdate" },
    
  ];
 
  return (
    <div  className= "table-size">
    
    <div className= "subheader">
             
        <h1 style={{"color": "black", "margin-bottom":"0px"}}>Jobs </h1><span>&nbsp;&nbsp;&nbsp;&nbsp;</span>
      
        <div className="toggle-switch">
          <Checkbox
            toggle
            onClick={() => toggleselected(!selected)}
            checked={selected}
          />
        </div>
        <div className="archived">Show Completed</div>
      </div>

      <div className="equipment-table">
        <MaterialTable
          columns={columns}
          data={selected ? jobData_withcomplete : jobData_withoutcomplete}
          editable={{
            onRowAdd:(newRow)=> new Promise((resolve,reject)=>{}),
            onRowUpdate:(newRow,oldRow)=> new Promise(()=>{}),
            onRowDelete:(selectedRow)=> new Promise(()=>{})
          }}
          options={{
            showTitle: false,
            paging: true,
            pageSizeOptions: [2, 5, 10, 15, 20],
            paginationType: "stepped",
            showFirstLastPageButtons: false,
            filtering: true,
            actionsColumnIndex:-1
          }}
          icons={{
          
            Add: () => <AddCircleRoundedIcon fontSize="large" color="primary" />,
            Edit: () => <CreateIcon color="action" />,
            Delete: () => <DeleteIcon color="action" />
          }}
        ></MaterialTable>
      </div>
    </div>
  );
}
