import React from 'react'
import MaterialTable from 'material-table';
import CreateIcon from '@mui/icons-material/Create';
import AddCircleRoundedIcon from '@mui/icons-material/AddCircleRounded';
import DeleteIcon from '@mui/icons-material/Delete';

export default function SiloTable() {
  
    

    const [data, setData] = React.useState([
      { name: 'Linta Paddock', capacity: '23', unit:'m3',surface: '10', unit:'m2',depth: '23', unit:'m3', slop: 'fallow', notes: 'N' },
      { name: 'K2K Paddock', capacity: '50', unit:'f3', surface: '23', unit:'f2',depth: '23', unit:'m3',slop: 'dfg', notes: 'C' },
      { name: 'Harry Woods Paddock', capacity: '40', unit:'kl', surface: '40', unit:'m2',depth: '23', unit:'m3',slop: 'abc', notes: 'T' },
      { name: 'Peter Dein Co.', capacity: '10', unit:'Ml', surface: '2', unit:'ha',depth: '23', unit:'m3',slop: 'abc', notes: 'TS' }
       ])
  const columns = [{
      title: 'Name', field: 'name'
  },
  {
      title: 'Capacity', field: 'capacity', type: 'numeric',
  },
  {
      title: 'Unit', field: 'unit',
      lookup: { 'l': 'Litres', 'kl': 'Kilolitres', 'Ml': 'Megalitres', 'G': 'Gallons', 'm3':'Cubic metre', 'f3':'Cubic feet','y3':'Cubic yard'},
  },{
    title: 'Surface Area', field: 'surface', type: 'numeric',
},
{
    title: 'Unit', field: 'unit',
    lookup: { 'ha': 'Hectares', 'ac': 'Acres', 'm2':'Square metre', 'f2':'Square feet'},
},{
  title: 'Max Depth ', field: 'depth', type: 'numeric',
  
},
{
  title: 'Unit', field: 'unit',
  lookup: { 'ha': 'Hectares', 'ac': 'Acres', 'm2':'Square metre', 'f2':'Square feet'},
}, {
      title: 'slop', field: 'slop', type: 'numeric', 
      
  }, {
      title: 'notes', field: 'notes',
    
  }]
    return (
        <div>
            <MaterialTable title="Silos"
                data={data}
                columns={columns}
                editable={{
                
                        onRowAdd: newData =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              setData([...data, newData]);
              
              resolve();
            }, 1000)
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              const dataUpdate = [...data];
              const index = oldData.tableData.id;
              dataUpdate[index] = newData;
              setData([...dataUpdate]);

              resolve();
            }, 1000)
          }),
          
        onRowDelete: oldData =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              const dataDelete = [...data];
              const index = oldData.tableData.id;
              dataDelete.splice(index, 1);
              setData([...dataDelete]);
              
              resolve()
            }, 1000)
          }),
                }}
               
                options={{
                    actionsColumnIndex: -1,
                    filtering: true,
                    paging: true,
                    pageSizeOptions: [2, 5, 10, 15, 20],
                    paginationType: "stepped",
                    showFirstLastPageButtons: false,
                }}
                icons={{
                 
                  Add: () => <AddCircleRoundedIcon fontSize="large" color="primary" />,
                  Edit: () => <CreateIcon color="action" />,
                  Delete: () => <DeleteIcon color="action" />
                }}
            /> 
        </div>
    )
}
